/* @flow weak */

const knit = require('@knit/knit-core');

const tasks = [
  {
    title: 'discovering modules',
    task: ctx => {
      const modules = knit.findPublicModules();
      ctx.modules = modules;
      if (ctx.forceAll) {
        ctx.updated = modules;
      }
    },
  },
];

module.exports = tasks;
