#!/usr/bin/env node

const Listr = require('listr');

const errors = require('../lib/errors');
const log = require('../lib/log');

module.exports = (version, opts) => {
  new Listr([
    ...require('../tasks/preflight/version'),
    ...require('../tasks/preflight/git'),
    ...require('../tasks/preflight/yarn'),
    ...require('../tasks/modules'),
    ...require('../tasks/preflight/knit'),
    ...require('../tasks/packages'),
    ...require('../tasks/updated'),
    ...require('../tasks/version'),
  ], {
    renderer: log.getRenderer(),
    collapse: false,
  }).run({ version, forceAll: opts.forceAll }).catch(errors.catchErrors);
};
