const logger = require('@knit/mittens-logger');

try {
  module.exports = require('@knit/mittens-develop');
} catch (err) {
  exports.command = 'dev';
  exports.describe = 'built-in developer tools [Not Installed]';
  exports.handler = () => {
    console.log();
    logger.warning('Developer tools not installed');
    logger.warning('yarn add --dev @knit/mittens-develop');
  };
}
