const logger = require('@knit/mittens-logger');

try {
  module.exports = require('@knit/mittens-publish');
} catch (err) {
  exports.command = 'pb';
  exports.describe = 'built-in publishing tools [Not Installed]';
  exports.handler = () => {
    console.log();
    logger.warning('Publishing tools not installed');
    logger.warning('yarn add --dev @knit/mittens-publish');
  };
}
