/* @flow weak */

const chalk = require('chalk');

exports.info = (...msg) => console.log(chalk.blue('info'), ...msg);
exports.warning = (...msg) => console.log(chalk.yellow('warning'), ...msg);
exports.error = (...msg) => console.log(chalk.red('error'), ...msg);
exports.success = (...msg) => console.log(chalk.green('success'), ...msg);
exports.missing = (...msg) => console.log(chalk.red('missing'), ...msg);
exports.subtree = (...msg) => console.log(chalk.white('└─'), ...msg);
