/* @flow weak */

const path = require('path');

exports.isScoped = (module) => module[0] === '@'

exports.getModuleName = (module) => (
  exports.isScoped(module) ? module.split('/')[1] : module
)

exports.safeFindPkg = (dir) => {
  try {
    // $FlowIgnore
    return require(path.join(dir, 'package.json'));
  } catch (err) {
    return null;
  }
};
