/* @flow weak */

const execa = require('execa');

const needle = require('@knit/needle');

const knit = require('@knit/knit');

const tasks = [
  {
    title: 'discovering modules',
    task: ctx => {
      const modules = knit.findModules();
      ctx.modules = modules;
      if (ctx.forceAll) {
        ctx.updated = modules;
      }
    },
  },
];

module.exports = tasks;
