/* @flow weak */

const execa = require('execa');

const utils = require('@knit/knit/lib/utils');

const tasks = [
  {
    title: 'reading package.json of modules',
    skip: ctx => ctx.pkgs,
    task: ctx => {
      ctx.pkgs = {}
      ctx.modules.map(m => {
        ctx.pkgs[m] = utils.safeFindPkg(m)
      })
    }
  },
];

module.exports = tasks;
