/* @flow */

const execa = require('execa');
const Listr = require('listr');
const semver = require('semver');

const knit = require('../..');
const needle = require('@knit/needle');

const tasks = [
  {
    title: 'validating package.json',
    task: () => {
      ['version', 'repository'].forEach(field => {
        if (!needle.pkg[field]) {
          throw {
            cmd: 'incomplete package.json',
            stderr: `need to set \`${field}\` field in package.json`,
          };
        }
      });
    },
  },
  {
    title: 'check for missing dependencies',
    task: () => knit.findAllMissingDependencies(
        knit.findModules()
      ).then(m => {
        if (m.length > 0) {
          throw {
            cmd: `found ${m.length} missing package${m.length > 1 ? 's' : ''}`,
            stderr: m.join(' '),
          }; }
        return true;
      }),
  },
  {
    title: 'check for unused dependencies',
    task: () => knit.findAllUnusedDependencies(
        knit.findModules()
      ).then(m => {
        if (m.length > 0) {
          throw {
            cmd: `found ${m.length} unused package${m.length > 1 ? 's' : ''}`,
            stderr: m.join(' '),
          }; }
        return true;
      }),
  },
];

module.exports = tasks;
