/* @flow weak */

const execa = require('execa');

const knit = require('@knit/knit-core');

const tasks = [
  {
    title: 'reading package.json of modules',
    task: ctx => {
      ctx.pkgs = {};
      ctx.modules.map(m => {
        ctx.pkgs[m] = knit.readPkg(m);
      });
    },
  },
];

module.exports = tasks;
