/* @flow weak */

const jest = require('jest');

module.exports = (argv) => {
  argv._.splice(0, 2);
  const opts = argv._;

  if (process.env.CI === true) {
    opts.push('--bail');
    opts.push('--coverage');
  }

  if (process.env.NODE_ENV == null) {
    process.env.NODE_ENV = 'test';
  }

  jest.run(opts);
};
