/* @flow weak */

module.exports = (opts) => {
  if (opts.port && !process.env.PORT) {
    process.env.PORT = opts.port;
  }
  if (opts.host && !process.env.HOST) {
    process.env.HOST = opts.host;
  }
  if (opts.proxy && !process.env.PROXY) {
    process.env.PROXY = opts.proxy;
  }

  require('../tasks/dev-server');
};
